INSERT INTO reviews VALUE('Elizabeth Fisher',-1,4,-1,'Prof. Pepiot is a great professor - her notes are really nice and lectures are generally helpful. Homeworks and exams are difficult, but overall a good class.','MAE 2210',1197);
